/* Function name      : sci1Puts
 * Function arguments : short data
 * Return value       : none
 * Header file        : sci.h 
 *
 * Function desciption:
 * sci1Puts uses sci1Putc to send a string of characters to SCI1 port.
 */
void sci1Puts(char *buff)
{
  while (*buff != '\0') {
    if (sci1Putc(*buff) == 1)
      buff++;
  }
}

/* Function name      : sci1Write
 * Function arguments : char *, short 
 * Return value       : none
 * Header file        : sci.h 
 *
 * Function desciption:
 * sci1Puts uses sci1Putc to send a string of characters to SCI1 port.
 */
void sci1Write(char *buff, short n)
{
  short i;

  for(i = 0 ;i < n ; i++)
  {
    if (sci1Putc(*buff) == 1)
      buff++;
  }

}


/* Function name      : enqueueTx
 * Function arguments : short data
 * Return value       : short
 * Header file        : sci.h 
 *
 * Function desciption:
 * enqueueTx is a function used to access txFifo QUEUE structure.
 * enqueueTx returns 1 for successful enqueue of variable data
 * and returns -1 for failed enqueue.
 */
short enqueueTx1(short data)
{
  short retVal = 1;

  txFifo.semaphore = 1;
  retVal = enqueue(&txFifo,data);

  if(SCI1.SSR.BIT.TDRE == 1) {
      txFifo.semaphore = 0;
      SCI1.TDR = dequeue(&txFifo); 
  }

  txFifo.semaphore = 0;

  return retVal;
} 

